const hamburger = document.querySelector('.hamburger'),
  navList = document.querySelector('.nav-list'),
  navListItem = document.querySelectorAll('.nav-list-item'),
  sectionNav = document.querySelector('.section-nav'),
  sectionHero = document.querySelector('.section-hero');
hamburger.addEventListener('click', (e) => {
  slideMobileNav(), sectionNav.classList.toggle('bg-white');
}),
  navListItem.forEach((e) =>
    e.addEventListener('click', () => {
      slideMobileNav();
    })
  );
const obs = new IntersectionObserver(
  (e) => {
    let s = e[0];
    !1 === s.isIntersecting &&
      (sectionNav.classList.add('sticky'),
      sectionHero.classList.add('section-mt-10'));
  },
  { root: null, threshold: 0 }
);
function slideMobileNav() {
  hamburger.classList.toggle('active'), navList.classList.toggle('active');
}
obs.observe(sectionHero),
  window.addEventListener('scroll', function () {
    if (sectionNav.classList.contains('bg-pastel')) {
      0 === window.scrollY &&
        (sectionNav.classList.remove('bg-pastel'),
        sectionNav.classList.remove('sticky'),
        sectionHero.classList.remove('section-mt-10'));
    } else {
      sectionNav.classList.add('bg-pastel');
    }
  });
